mapper.select:
select c.id,c.CLUB_NAME,c.CLUB_CREATOR,c.LEADER_ID,c.CLUB_INTRODUCE,c.CLUB_IMG,c.IS_DELETE,c.CREATE_TIME,c.UPDATE_TIME, c.CLUB_DESC,c.STATUS, u.AMOUNT AS AMOUNT from club c left join (select count(1) as AMOUNT,CLUB_ID from user where status =0 GROUP BY CLUB_ID) u on u.CLUB_ID = c.id where 1=1
and c.STATUS = #{status}
and c.LEADER_ID = #{leaderId}
GROUP BY c.id ORDER BY c.CREATE_TIME desc
